from application import db, appfrom common.libs.wechatService import WeChatServiceimport requests, jsonclass CustomerService:    weChatService = WeChatService()    def __init__(self):        pass    def __cuslist__(self, cursor):        accesstoken = self.weChatService.getAccessToken()        url = 'https://dopen.weimob.com/api/1_0/mc/member/getMemberList?accesstoken={0}'.format(accesstoken)        data = {            'cursor': cursor,            'size': 100        }        r = requests.post(url, json=data)        if r.status_code != 200 or not r.text:            return 400        data = json.loads(r.text)        return data['data']    def __cusinfo__(self, wid):        accesstoken = self.weChatService.getAccessToken()        url = 'https://dopen.weimob.com/api/1_0/mc/member/getMemberDetail?accesstoken={0}'.format(accesstoken)        data = {            'wid': wid,            'isNeedTagsInfo': False,            'isNeedExtInfo': False        }        r = requests.post(url, json=data)        if r.status_code != 200 or not r.text:            return 400        data = json.loads(r.text)        return data['data']    def __cusops__(self, cus):        accesstoken = self.weChatService.getAccessToken()        url = 'https://dopen.weimob.com/api/1_0/mc/member/updateMemberInfo?accesstoken={0}'.format(accesstoken)        data = cus        r = requests.post(url, json=data)        if r.status_code != 200 or not r.text:            return 400        data = json.loads(r.text)        return data['data']