from application import app, dbfrom common.libs.wechatService import WeChatServiceimport requests, jsonclass GoodsService:    weChatService = WeChatService()    def __init__(self):        pass    def getGoodsList(self, pageNum, pageSize):        accesstoken = self.weChatService.getAccessToken()        url = 'https://dopen.weimob.com/api/1_0/ec/goods/queryGoodsList?accesstoken={0}'.format(accesstoken)        data = {            'pageNum': pageNum,            'pageSize': pageSize,            'orderBy': {                'field': 'salesNum',                'sort': 'desc'            },            'queryParameter': {                'goodsStatus': 0,            }        }        r = requests.post(url, json=data)        if r.status_code != 200 or not r.text:            return 400        data = json.loads(r.text)        return data['data']    def __goodinfo__(self, goodsId):        accesstoken = self.weChatService.getAccessToken()        url = 'https://dopen.weimob.com/api/1_0/ec/goods/queryGoodsDetail?accesstoken={0}'.format(accesstoken)        data = {            'goodsId': goodsId        }        r = requests.post(url, json=data)        if r.status_code != 200 or not r.text:            return 400        data = json.loads(r.text)        return data['data']