from application import appimport random, stringclass MachineService:    def __init__(self):        pass    def __createcardcode__(self):        src_digits = string.digits  # string_数字        src_uppercase = ['A', 'B', 'C', 'D', 'E', 'F']  # string_大写字母        for i in range(8):            # 随机生成数字、大写字母、小写字母的组成个数（可根据实际需要进行更改）            digits_num = random.randint(1, 4)            uppercase_num = random.randint(1, 8 - digits_num)            # 生成字符串            password = random.sample(src_digits, digits_num) + random.sample(src_uppercase,                                                                             uppercase_num)            # 打乱字符串            random.shuffle(password)            # 列表转字符串            new_password = ''.join(password)            return new_password    def __saveorderinfo__(self):        pass