from application import app, dbfrom common.model.wechat.Message import Messagefrom common.libs.WebHelper import getCurrentDateclass MessageService:    def __init__(self):        pass    def __createMsg__(self, orderNo, event, topic):        msg = Message()        msg.created_time = getCurrentDate()        msg.orderNo = orderNo        msg.event = event        msg.topic = topic        db.session.add(msg)        db.session.commit()        return True