from application import app, dbfrom common.libs.wechatService import WeChatServiceimport requests, jsonclass OrderService:    weChatService = WeChatService()    def __init__(self):        pass    def __orderlist__(self, pageNum, pageSize):        accesstoken = self.weChatService.getAccessToken()        url = 'https://dopen.weimob.com/api/1_0/ec/order/queryOrderList?accesstoken={0}'.format(accesstoken)        data = {            'pageNum': pageNum,            'pageSize': pageSize,            'queryParameter': {}        }        r = requests.post(url, json=data)        if r.status_code != 200 or not r.text:            return 400        data = json.loads(r.text)        return data['data']    def __orderinfo__(self, orderNo):        accesstoken = self.weChatService.getAccessToken()        url = 'https://dopen.weimob.com/api/1_0/ec/order/queryOrderDetail?accesstoken={0}'.format(accesstoken)        data = {            'orderNo': orderNo        }        r = requests.post(url, json=data)        if r.status_code != 200 or not r.text:            return 400        data = json.loads(r.text)        return data['data']    def __cancelorder__(self, orderNo, reason):        accesstoken = self.weChatService.getAccessToken()        url = 'https://dopen.weimob.com/api/1_0/ec/order/cancelOrder?accesstoken={0}'.format(accesstoken)        data = {            'orderNo': orderNo,            'specificCancelReason': reason        }        r = requests.post(url, json=data)        if r.status_code != 200 or not r.text:            return 400        data = json.loads(r.text)        return data['data']