from sqlalchemy import Column, DateTime, Integer, String, DECIMALfrom sqlalchemy.schema import FetchedValuefrom application import dbclass Customer(db.Model):    __tablename__ = 'customers'    id = db.Column(Integer, primary_key=True)    mid = db.Column(Integer, nullable=False, server_default=FetchedValue())    phone = db.Column(String(32), nullable=False, index=True, server_default=FetchedValue())    code = db.Column(String(128), nullable=False, server_default=FetchedValue())    wid = db.Column(String(600), nullable=False, server_default=FetchedValue())    gmtCreate = db.Column(DateTime, nullable=False, server_default=FetchedValue())    cardPublishTime = db.Column(DateTime, nullable=False, server_default=FetchedValue())    name = db.Column(String(32), nullable=False, server_default=FetchedValue())    birthday = db.Column(DateTime, nullable=False, server_default=FetchedValue())    idCard = db.Column(String(32), nullable=False, server_default=FetchedValue())    address = db.Column(String(256), nullable=False, server_default=FetchedValue())    mail = db.Column(String(64), nullable=False, server_default=FetchedValue())    sex = db.Column(Integer, nullable=False, server_default=FetchedValue())    education = db.Column(String(32), nullable=False, server_default=FetchedValue())    hobby = db.Column(String(32), nullable=False, server_default=FetchedValue())    income = db.Column(String(132), nullable=False, server_default=FetchedValue())    industry = db.Column(String(132), nullable=False, server_default=FetchedValue())    logo = db.Column(String(132), nullable=False, server_default=FetchedValue())    wechatStatus = db.Column(Integer, nullable=False, server_default=FetchedValue())    wechatCode = db.Column(String(132), nullable=False, server_default=FetchedValue())    totalPoint = db.Column(Integer, nullable=False, server_default=FetchedValue())    currentPoint = db.Column(Integer, nullable=False, server_default=FetchedValue())    currentAmount = db.Column(Integer, nullable=False, server_default=FetchedValue())    currentGrowth = db.Column(Integer, nullable=False, server_default=FetchedValue())    orderConsumeAmount = db.Column(DECIMAL, nullable=False, server_default=FetchedValue())    orderConsumeCount = db.Column(Integer, nullable=False, server_default=FetchedValue())    avgOrderConsumeAmount = db.Column(DECIMAL, nullable=False, server_default=FetchedValue())    lastTradeTime = db.Column(DateTime, nullable=False, server_default=FetchedValue())    tagList = db.Column(String(600), nullable=False, server_default=FetchedValue())    tagId = db.Column(Integer, nullable=False, server_default=FetchedValue())    tagName = db.Column(String(128), nullable=False, server_default=FetchedValue())    rankName = db.Column(String(128), nullable=False, server_default=FetchedValue())    rankId = db.Column(Integer, nullable=False, server_default=FetchedValue())    status = db.Column(Integer, nullable=False, server_default=FetchedValue())    extInfoList = db.Column(String(600), nullable=False, server_default=FetchedValue())    key = db.Column(String(128), nullable=False, server_default=FetchedValue())    value = db.Column(String(128), nullable=False, server_default=FetchedValue())