from sqlalchemy import Column, DateTime, Integer, Stringfrom sqlalchemy.schema import FetchedValuefrom application import dbclass Machine_CardCode(db.Model):    __tablename__ = 'machine_cardcode'    id = db.Column(Integer, primary_key=True)    order_id = db.Column(String(600), nullable=False, server_default=FetchedValue())    vmc = db.Column(String(128), nullable=False, index=True, server_default=FetchedValue())    pid = db.Column(Integer, nullable=False, server_default=FetchedValue())    code = db.Column(String(128), nullable=False, server_default=FetchedValue())    createtime = db.Column(DateTime, nullable=False, server_default=FetchedValue())    msg = db.Column(String(600), nullable=False, server_default=FetchedValue())