from sqlalchemy import Column, DateTime, Integer, Stringfrom sqlalchemy.schema import FetchedValuefrom application import dbclass Message(db.Model):    __tablename__ = 'weimob_message'    id = db.Column(Integer, primary_key=True)    orderNo = db.Column(String(600), nullable=False, server_default=FetchedValue())    topic = db.Column(DateTime, nullable=False, index=True, server_default=FetchedValue())    created_time = db.Column(DateTime, nullable=False, server_default=FetchedValue())    event = db.Column(String(600), nullable=False, server_default=FetchedValue())    msg_body = db.Column(String(600), nullable=False, server_default=FetchedValue())