from web.controller.index import route_apifrom common.libs.customer.customerService import CustomerServicefrom flask import jsonify, requestcusService = CustomerService()@route_api.route('/customer')def cus_list():    resq = {'code': 200, 'msg': '操作成功', 'list': {}}    arg = request.args    cursor = arg.get('cursor')    list = cusService.__cuslist__(1)    if list == 400:        resq['code'] = 400,        resq['msg'] = '数据异常'        return jsonify(resq)    resq['list'] = list    return jsonify(resq)@route_api.route('customer/info')def cus_Info():    resq = {'code': 200, 'msg': '操作成功', 'info': {}}    arg = request.args    wid = arg.get('wid')    info = cusService.__cusinfo__(wid)    if list == 400:        resq['code'] = 400,        resq['msg'] = '数据异常'        return jsonify(resq)    resq['info'] = info    return jsonify(resq)@route_api.route('customer/update', methods=['POST'])def cus_ops():    resq = {'code': 200, 'msg': '操作成功', 'info': {}}    req = request.values    data = req['info'] if 'info' in req else ''    info = cusService.__cusops__(data)    if list == 400:        resq['code'] = 400,        resq['msg'] = '数据异常'        return jsonify(resq)    resq['info'] = info    return jsonify(resq)