from web.controller.index import route_apifrom common.libs.goods.goodsService import GoodsServicefrom flask import jsonify, request@route_api.route('/goods')def goods_list():    resq = {'code': 200, 'msg': '操作成功', 'list': {}}    req = request.values    pageNum = req['pageNum'] if 'pageNum' in req else 1    goodsService = GoodsService()    list = goodsService.getGoodsList(pageNum, 20)    if list == 400:        resq['code'] = 400,        resq['msg'] = '数据异常'        return jsonify(resq)    resq['list'] = list    return jsonify(resq)@route_api.route('/goods/info')def goods_info():    resq = {'code': 200, 'msg': '操作成功', 'info': {}}    req = request.values    goodsid = req['goodsid'] if 'goodsid' in req else 1    goodsService = GoodsService()    info = goodsService.__goodinfo__(goodsid)    if list == 400:        resq['code'] = 400,        resq['msg'] = '数据异常'        return jsonify(resq)    resq['info'] = info    return jsonify(resq)