from web.controller.index import route_apifrom common.libs.messageService import MessageServicefrom common.libs.machineService import MachineServicefrom flask import jsonify, request@route_api.route('/message', methods=['POST'])def msg_get():    resp = {'code': {'errorcode': 0, 'errmsg': 'success'}}    req = request.values    topic = req['topic']    event = req['event']    msg_body = req['msg_body']    orderNo = msg_body['orderNo']    msg = MessageService()    msg.__createMsg__(orderNo, event, topic)    if event == 'orderStatusChange':        mar = MachineService()        password = mar.__createcardcode__()    return jsonify(resp)@route_api.route('/cardcode', methods=['POST'])def cardcode_valid():    resp = {'resultCode': 1, 'reason': ''}    mar = MachineService()    password = mar.__createcardcode__()    # req = request.values    # machineCode = req['machineCode']    # PID = req['PID']    # CODE = req['CODE']    # mac = req['MAC']    return password