from web.controller.index import route_apifrom common.libs.order.orderService import OrderServicefrom flask import jsonify, request@route_api.route('/order')def order_list():    resq = {'code': 200, 'msg': '操作成功', 'list': {}}    req = request.values    pageNum = req['pageNum'] if 'pageNum' in req else 1    orderService = OrderService()    list = orderService.__orderlist__(pageNum, 20)    if list == 400:        resq['code'] = 400,        resq['msg'] = '数据异常'        return jsonify(resq)    resq['list'] = list    return jsonify(resq)@route_api.route('/order/info')def order_info():    resq = {'code': 200, 'msg': '操作成功', 'info': {}}    req = request.values    orderNo = req['orderNo'] if 'orderNo' in req else ''    orderService = OrderService()    info = orderService.__orderinfo__(orderNo)    if list == 400:        resq['code'] = 400,        resq['msg'] = '数据异常'        return jsonify(resq)    resq['info'] = info    return jsonify(resq)@route_api.route('/order/cancel', methods=['POST'])def order_cancel():    resq = {'code': 200, 'msg': '操作成功', 'info': {}}    req = request.values    orderNo = req['orderNo'] if 'orderNo' in req else ''    reason = req['reason'] if 'reason' in req else ''    orderService = OrderService()    info = orderService.__cancelorder__(orderNo, reason)    if list == 400:        resq['code'] = 400,        resq['msg'] = '数据异常'        return jsonify(resq)    resq['info'] = info    return jsonify(resq)